    session_start();

	$_SESSION["dice"]=1;  
	create_dices(isset($_GET["but"]) ? $_GET["but"] : 0 );
	function create_dices($but=0){
		$dat=array();
		$dat[0] = "";
		$dat[1] = "";
		$dat[2] = "";
		$dat[3] = "";
		$dat[4] = "";
		$dat[5] = "";
		$dat[6] = "";
		$dat[7] = 50;
		$dat[8] = 43;
		
		if($but==0){
			$image_size_array=array($dat[count($dat) - 2],$dat[count($dat) - 1]);
			$s_width=$image_size_array[0];
			$s_height=$image_size_array[1];
			$width=$s_width*4;
			$height=$image_size_array[1];
			$image=imagecreate($width,$height);
			$x_offset=0;
			$counter=null;
			for($i=0;$i<4;$i++){
				$rand_num=rand(1,6);
					$counter=$counter+$rand_num;
				$temp=imagecreatefromstring(base64_decode($dat[$rand_num]));
				imagecopymerge($image,$temp,$x_offset,0,0,0,$s_width,$s_height,100);
				$x_offset=$x_offset+$s_width;
			}
			$_SESSION["dice"]=$counter;
			header("Content-type: image/png");
			imagepng($image);
			imagedestroy($image);
			imagedestroy($temp);
		}
		else{
				$image=imagecreatefromstring(base64_decode($dat[0]));
				header("Content-type: image/png");
				imagepng($image);
				imagedestroy($image);
				imagedestroy($temp);
		}
	}
?>  
		